// Fazer escrita uma vez e fazer leitura com dois processos ao mesmo tempo. Comparar as duas leituras no final. 

#include "../fs/operations.h"
#include <assert.h>
#include <string.h>
#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <pthread.h>

//char* a = "";


void* thr_func(void* arg){

	char* a = (char*) arg;
	char *path = "/f1";
	int f = tfs_open(path, 0);
	char buffer[1001];
	tfs_read(f, buffer, 1000);
	assert(strcmp(buffer, a) == 0);
	tfs_close(f);

	return NULL;
}

int main(){
	
	pthread_t tid[300];
	assert(tfs_init() != -1);

	char *path = "/f1";
	int f = tfs_open(path, TFS_O_CREAT);

	char a[1001];
	memset(a, 'A', 1000);

	tfs_write(f, a, strlen(a));
	tfs_close(f);

	for (int i = 0; i < 300; i++)
		pthread_create(&tid[i], NULL, thr_func, (void*) &a);

	for (int i = 0; i < 300; i++)
		pthread_join(tid[i], NULL);

    printf("Successful test.\n");
	return 0;
}